mapper.select:
select sum(money)
from minus
where id = #{id}

select sum(money)
from minus
where d = #{d}

select money, id, content, d, mno
from minus
where d = #{d}

select money
from minus
where id = #{id}

mapper.update:
update minus
set money = #{money}, content = #{content}
where id = #{id} and mno = #{mno}
