mapper.select:
select sum(money)
from plus
where id = #{id}

select money, content
from plus
where d = #{d}

select money
from plus
where id = #{id}

select money, id, content, d, pno
from plus
where d = #{d}

mapper.update:
update plus
set money = #{money}, content = #{content}
where id = #{id} and mno = #{pno}
